import { Component, OnInit } from '@angular/core';

import { LaunchesService } from 'src/app/services/launches.service';

@Component({
  selector: 'app-spacex-launches',
  templateUrl: './spacex-launches.component.html',
  styleUrls: ['./spacex-launches.component.css']
})
export class SpacexLaunchesComponent {
  launches: any[] = [];
  filteredLaunches: any[] = [];

  constructor(private launchesService: LaunchesService){}


  ngOnInit(): void{
    this.launchesService.getLaunches().subscribe(data =>{
      this.launches = data;
      this.filteredLaunches = data;

    });
  }
  filterSuccess(success: boolean): void{
    this.filteredLaunches = this.launches.filter(launch => launch.success === success);

  }
}
