import { Injectable } from '@angular/core';
import {HttpClient} from '@angular/common/http';
import { Observable } from 'rxjs';
import { Album, AlbumPhotos } from '../models/models';
import { of } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class AlbumsService {

  BASE_URL = 'https://jsonplaceholder.typicode.com';

  constructor(private client: HttpClient) { }

  getAlbums() {
    return of([
      { id: 1, title: 'Семейный альбом', body: 'Описание 1' },
      { id: 2, title: 'Путешествия', body: 'Описание 2' },
      { id: 3, title: 'Любимые моменты', body: 'Описание 3' },
      { id: 4, title: 'Природа', body: 'Описание 4' },
      { id: 5, title: 'Друзья', body: 'Описание 5' }
    ]);
  }

  getAlbum(id: number): Observable<Album> {
    return this.client.get<Album>(`${this.BASE_URL}/albums/${id}`);
  }

  addAlbum(album: Album): Observable<Album> {
    return this.client.post<Album>(`${this.BASE_URL}/albums`, album);
  }

  updateAlbum(album: Album): Observable<Album> {
    return this.client.put<Album>(`${this.BASE_URL}/albums/${album.id}`, album);
  }

  deleteAlbum(id: number): Observable<any> {
    return this.client.delete(`${this.BASE_URL}/albums/${id}`);
  }

  getAlbumPhotos(id: number): Observable<AlbumPhotos[]> {
    return of([
      { id: 1, albumId: id, thumbnailUrl: 'assets/images/new-photo1.jpg', url: 'assets/images/new-photo1.jpg', title: 'New Photo 1' },
      { id: 2, albumId: id, thumbnailUrl: 'assets/images/new-photo2.jpg', url: 'assets/images/new-photo2.jpg', title: 'New Photo 2' },
      { id: 3, albumId: id, thumbnailUrl: 'assets/images/new-photo3.jpg', url: 'assets/images/new-photo3.jpg', title: 'New Photo 3' }
    ]);
  }
}
